<?phpclass Sonic_ActualPrice_Block_Adminhtml_Sales_Order_Totals extends Mage_Sales_Block_Order_Totals{	/**	* Initialize order totals array	*	* @return Mage_Sales_Block_Order_Totals	*/	protected function _initTotals()	{		parent::_initTotals();		$amount = 0;		$order = $this->getOrder();		$_items = $order->getItemsCollection();		foreach ($_items as $item) {			//do something			$amount = $amount + ($item->getActualPrice()*$item->getQtyOrdered());		}				if ($amount) {			$this->addTotal(new Varien_Object(array(				'code' => 'total_actual_price',				'value' => $amount,				'base_value'=> $amount,				'label' => $this->helper('actualprice')->__('Total Actual Price'),			)));		}		return $this;	}}